if (pb.authStore.isValid) {
    try {
        const collection = Inputs.collectionName;
        const page = Inputs.page || 1;
        const resultsPerPage = Inputs.resultsPerPage || 50;
        const filter = Inputs.filter || '';

        // Fetch a paginated records list
        const resultList = await pb.collection(collection).getList(Number(page), Number(resultsPerPage), {
            filter: filter,
        });

        Outputs.result = resultList;
        Outputs.records = resultList.items;
        Outputs.Success();

    } catch (error) {
        console.error('Error fetching records:', error);
        Outputs.Failure();
        Outputs.error = error;
    }
} else {
    Outputs.Failure();
    Outputs.error = 'User is not authenticated.'
}

try {
    // Validate the required inputs
    if (!Inputs.collectionName) {
        throw new Error('Collection name is required.');
    }
    if (!Inputs.dataObject) {
        throw new Error('Data object is required.');
    }

    // Retrieve the data object from the inputs
    const data = Noodl.Object.get(Inputs.dataObject);

    // Remove the `id` field if present (PocketBase usually generates it)
    if (data.id) {
        delete data.id;  // PocketBase will auto-generate the ID
    }

    const collection = Inputs.collectionName;

    // Log the data for debugging purposes
    console.log('Data being sent (without id):', data);

    // Create the new record in the specified collection
    const record = await pb.collection(collection).create(data);

    // Pass the created record to the output
    Outputs.record = record;
    Outputs.Success();

} catch (error) {
    // Log detailed error information from PocketBase
    console.error('Error creating record:', error);

    // Check if it's a ClientResponseError to extract more details
    if (error.response && error.response.data) {
        console.error('PocketBase error details:', error.response.data);
        Outputs.error = `Failed to create record: ${JSON.stringify(error.response.data)}`;
    } else {
        Outputs.error = error.message || 'Unknown error occurred';
    }

    Outputs.Failure();
}

try {
    // Log the inputs for debugging purposes
    console.log('Record ID:', Inputs.recordId);
    console.log('Collection:', 'Cats');
    console.log('Data before removing id:', Inputs.dataObject);

    // Retrieve the data object from the inputs
    const data = Noodl.Object.get(Inputs.dataObject);

    // Remove the `id` field from the data, as it's immutable and cannot be updated
    if (data.id) {
        delete data.id;
    }

    // Log the data after removing the `id` field for debugging purposes
    console.log('Data being sent for update (without id):', data);

    // Check if recordId is provided
    if (!Inputs.recordId) {
        throw new Error("recordId is required for updating a record.");
    }

    // Attempt to update the record in the specified collection
    const record = await pb.collection('Cats').update(Inputs.recordId, data);

    // Check if the record was successfully updated
    if (record) {
        Outputs.record = record;
        Outputs.Success();
    } else {
        Outputs.Failure();
        Outputs.error = "Failed to update record.";
    }

} catch (error) {
    // Log detailed error information from PocketBase
    console.error('Error updating record:', error);

    // Capture detailed error information from the server response
    if (error.response && error.response.data) {
        console.error('PocketBase error details:', error.response.data);
        Outputs.error = `Failed to update record: ${JSON.stringify(error.response.data)}`;
    } else {
        Outputs.error = error.message || 'Unknown error occurred';
    }

    Outputs.Failure();
}

const collection = Inputs.collectionName || "Cats";
const recordId = Inputs.recordId;

try {
    await pb.collection(collection).delete(recordId);
    Outputs.Success();
} catch (error) {
    Outputs.error = error;
    Outputs.Failure();
}

// Make sure you can click through parent div
// so it doesn't block like normal popups
Inputs.This.getDOMElement().style.pointerEvents = 'none'
Inputs.This.getDOMElement().parentElement.style.pointerEvents = 'none'

try {
    const authData = await pb.collection('users').authWithPassword(
        Inputs.email,
        Inputs.password
    );

    // If the authentication is successful
    Noodl.Objects.CurrentUser.token = pb.authStore.token;
    Noodl.Objects.CurrentUser.authenticated = true;
    Outputs.Success();

    // Log the auth store data for debugging
    console.log(pb.authStore.isValid);   // true if authenticated
    console.log(pb.authStore.token);     // JWT token
    console.log(pb.authStore.model.id);  // User ID of the authenticated user
} catch (e) {
    // If authentication fails, pass the error to the Outputs
    Outputs.error = e;  // Store the error in Outputs
    Outputs.Failure();  // Trigger the failure output
}
