const input = Noodl.Objects[Inputs.inputObject] || {};
const webhookId = Inputs.webhookId;
const urlPath = Inputs.testWebhook ? "webhook-test" : "webhook";
const n8nRoot = Inputs.n8nRoot;
const queryType = Inputs.queryType || "POST";

const url = `${n8nRoot}/${urlPath}/${webhookId}`;

console.log("n8n url = ",url)

try {
  const response = await fetch(url, {
    method: queryType,
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(input) // Send input as JSON in the body
  });

  // Check for response.ok to handle non-JSON responses or errors
  if (!response.ok) {
    throw new Error(`HTTP error! Status: ${response.status}`);
  }

  const data = await response.json();

  Outputs.rawJsonResponse = data;

  if (data.code === 200 || !data.code) {
    Outputs.ResponseData = data;
    Outputs.Success();
  } else {
    Outputs.error = data.response ? data.response : 'Unknown error';
    Outputs.Failure();
  }

} catch (error) {
  Outputs.error = error.message || error; // Handle and output error message
  Outputs.Failure();
}

