// Check if the user is authenticated
if (pb.authStore.isValid) {
    Noodl.Objects.CurrentUser.token = pb.authStore.token;
    Noodl.Objects.CurrentUser.authenticated = true;
} else {
    console.log('User is not authenticated');
}

try {
    // Validate the required inputs
    if (!Inputs.collectionName) {
        throw new Error('Collection name is required.');
    }
    if (!Inputs.dataObject) {
        throw new Error('Data object is required.');
    }
    if (!Inputs.action || (Inputs.action !== 'create' && Inputs.action !== 'update')) {
        throw new Error("Action must be specified as either 'create' or 'update'.");
    }

    // Retrieve the data object from the inputs
    const rawData = Noodl.Object.get(Inputs.dataObject);

    // Remove the `id` field if present (PocketBase generates it for new records)
    if (rawData.id) {
        delete rawData.id;
    }

    const collection = Inputs.collectionName;

    if (Inputs.action === 'update') {
        // Validate that recordId is provided for updates
        if (!Inputs.recordId) {
            throw new Error("recordId is required for updating a record.");
        }

        console.log(`Updating record in collection '${collection}' with ID: ${Inputs.recordId}`);

        // Filter out empty fields to avoid overwriting
        const data = Object.entries(rawData).reduce((acc, [key, value]) => {
            if (value !== null && value !== undefined && value !== '') {
                acc[key] = value;
            }
            return acc;
        }, {});

        // Log sanitized data for debugging
        console.log('Sanitized data for update:', data);

        // Attempt to update the record
        const record = await pb.collection(collection).update(Inputs.recordId, data);

        Outputs.record = record;
        Outputs.Success();
    } else if (Inputs.action === 'create') {
        console.log(`Creating new record in collection '${collection}'`);

        // Log the data for debugging purposes
        console.log('Data being sent for creation:', rawData);

        const record = await pb.collection(collection).create(rawData);

        Outputs.record = record;
        Outputs.Success();
    }
} catch (error) {
    // Log detailed error information from PocketBase
    console.error('Error handling record:', error);

    // Check if it's a ClientResponseError to extract more details
    if (error.response && error.response.data) {
        console.error('PocketBase error details:', error.response.data);
        Outputs.error = `Failed to handle record: ${JSON.stringify(error.response.data)}`;
    } else {
        Outputs.error = error.message || 'Unknown error occurred';
    }

    Outputs.Failure();
}

try {
    // Validate the required inputs
    if (!Inputs.collectionName) {
        throw new Error('Collection name is required for deletion.');
    }
    if (!Inputs.recordId) {
        throw new Error('Record ID is required for deletion.');
    }

    const collection = Inputs.collectionName;
    const recordId = Inputs.recordId;

    // DEBUG
    // console.log(`Deleting record with ID '${recordId}' from collection '${collection}'`);

    // Perform the delete operation
    await pb.collection(collection).delete(recordId);

    // DEBUG
    // console.log(`Successfully deleted record with ID '${recordId}' from collection '${collection}'`);
    Outputs.Success();
} catch (error) {
    // Log detailed error information
    console.error('Error deleting record:', error);

    // Capture error details in the Outputs
    Outputs.error = error.response?.data || error.message || 'Unknown error occurred';
    Outputs.Failure();
}

try {
    const authData = await pb.collection('users').authWithPassword(
        Inputs.email,
        Inputs.password
    );

    // If the authentication is successful
    Noodl.Objects.CurrentUser.token = pb.authStore.token;
    Noodl.Objects.CurrentUser.authenticated = true;
    Outputs.Success();

    // Log the auth store data for debugging
    console.log(pb.authStore.isValid);   // true if authenticated
    console.log(pb.authStore.token);     // JWT token
    console.log(pb.authStore.model.id);  // User ID of the authenticated user
} catch (e) {
    // If authentication fails, pass the error to the Outputs
    Outputs.error = e;  // Store the error in Outputs
    Outputs.Failure();  // Trigger the failure output
}

if (pb.authStore.isValid) {
    try {
        const collection = Inputs.collectionName;
        const page = Inputs.page || 1;
        const resultsPerPage = Inputs.resultsPerPage || 50;
        const filter = Inputs.filter || '';

        // Fetch a paginated records list
        const resultList = await pb.collection(collection).getList(Number(page), Number(resultsPerPage), {
            filter: filter,
        });

        Outputs.result = resultList;
        Outputs.records = resultList.items;
        Outputs.Success();

    } catch (error) {
        console.error('Error fetching records:', error);
        Outputs.Failure();
        Outputs.error = error;
    }
} else {
    Outputs.Failure();
    Outputs.error = 'User is not authenticated.'
}

